import React from 'react';
import './Cards.css'
export default function Card(props) {
  const btn = {
    backgroundColor: "#FF7010",
  }
  const text = {
    color: "#FF7010",
  }
  const {
    title, imgAddress, price, id, about, isSelect, isLiked
  } = props.productInfo;
  return (
    <div className='col-3'>
          <div className="card mt-5">
            <img src={imgAddress} alt="foto" className='card-img-top' />
            <div className="card-body">
              <h4>{title}</h4>
              <p>{about}</p>
            </div>
            <div className="card-footer border-0 bg-white d-flex justify-content-between">
              <button className="btn btn-md text-white" style={btn}>Выбрать</button>
              <h5 className='m-3' style={text}>{price}</h5>
            </div>
          </div>
      </div>
  )
}
